	<script>
		$(function() {
		//全选反选并且计算商品价格和数量
			$('#box').click(function () {
				var count_price = 0;
				var count_num   = 0;
				$('.checkbox').each(function () {
				if (this.checked) {
				this.checked = false
				} else {
				this.checked = true;
				count_price += parseInt($(this).attr('price'));
				count_num   += parseInt($(this).attr('num'));
				}
				});
				$('#count_price').html(count_price);
				$('#count_num').html(count_num);
			});

		//单机复选框时统计商品价格和数量
		$('.checkbox').click(function () {
			var count_price = parseInt($('#count_price').html());
			var count_num   = parseInt($('#count_num').html());
			if(this.checked == false){
				$('#count_price').html(count_price -= parseInt($(this).attr('price')));
				$('#count_num').html(count_num -= parseInt($(this).attr('num')));
			}else{
				$('#count_price').html(count_price += parseInt($(this).attr('price')));
				$('#count_num').html(count_num += parseInt($(this).attr('num')));
			}
		});

