    (function(cfg) {
        function setPlaceholder(val) {
            $('#key').val(val)
                    .bind('focus',function(){
                        if (this.value==val){ this.value='';this.style.color='#333' }
                    })
                    .bind('blur',function(){
                        if (this.value==''){ this.value=val;this.style.color='#999' }
                    });
        }
        function render(r) {
            if (!r || !r.length) return;
            var html = '';
            var el = document.getElementById('hotwords')

            for (var i = 0; i < r.length; i++) {
                var item = r[i];

                if (i === 0) {
                    setPlaceholder(item.name)
                } else {
                    html += '<a target="_blank" data-gid="'+ item.gid +'" data-id="'+ item.id +'" href="'+ item.url_info +'">'+ item.name +'</a>'
                }
            }

            if (el) el.innerHTML = html
        }
        $('#hotwords').delegate('[data-id]', 'click', function () {
            var $this = $(this);
            window.log && window.log('hotWord', 'cmsPortal',
                    'hotWord_' + $this.data('id'), $this.text(), $this.data('gid'));
        })
        $.ajax({
            url: '//cds.3.cn/hotwords/get',
            data: { cate: cfg.cat.join(',') },
            dataType: 'jsonp',
            success: render
        })
    })(pageConfig.product);


    (function(cfg, $) {
        var $el = $('#settleup-2014')
        if (!$el.length || !cfg.isFlimPrint) return

        function setText() {
            $el.find('.ci-left').css('background', 'url(//img14.360buyimg.com/devfe/jfs/t5743/181/1120770505/1119/87a6dfd2/5923fe0eN563d2b15.png) 0 -1px')
            $el.find('.cw-icon a')
                    .text('我的冲印袋')
                    .attr('href', 'http://pcprinting.jd.com/printingBag/goToPrintingBag')
        }

        function setCount() {
            seajs.use('JDF_UNIT/login/1.0.0/login', handleLogin)

            function handleLogin(Login) {
                Login.isLogin(function(isLogin) {
                    if (isLogin) {
                        getCount();
                    }
                })
            }
            function getCount() {
                $.ajax({
                    url: '//printing.jd.com/printingBag/getPrintingBagCount',
                    dataType: 'jsonp',
                    success: function(r) {
                        if (r && r.success && r.result) {
                            $el.find('.cw-icon .ci-right').after('<i class="ci-count" id="shopping-amount">'+ r.result.count +'</i>');
                        }
                    }
                })
            }
        }

        setTimeout(function() {
            setText();
            setCount();
        }, 500)
    })(pageConfig.product, jQuery);
